import React from 'react';
import { motion } from 'framer-motion';

const Products = () => {
  const products = [
    {
      id: 1,
      name: "100% Natural Moisture Cream",
      description: "Collagen booster with certified organic ingredients for deep hydration and skin renewal",
      image: "/images/products/moisture-cream.jpg",
      price: "$52",
      features: ["100% Natural", "Collagen Booster", "Certified Organic", "50 ML"]
    },
    {
      id: 2,
      name: "SPF 50+ Sunscreen",
      description: "Broad spectrum UVA + UVB protection with natural ingredients for daily sun care",
      image: "/images/products/sunscreen.jpg",
      price: "$38",
      features: ["SPF 50+", "UVA + UVB Filter", "100% Natural", "50 ML"]
    },
    {
      id: 3,
      name: "Purifying Cleanser",
      description: "Gentle daily cleanser with organic extracts for clean, refreshed skin",
      image: "/images/products/cleanser.jpg",
      price: "$45",
      features: ["100% Natural", "Gentle Formula", "Certified Organic", "50 ML"]
    }
  ];

  return (
    <section id="products" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-light text-neoshe mb-6">
            Our Signature Collection
          </h2>
          <p className="text-lg text-gray-600 max-w-2xl font-light mx-auto">
            Three essential products for a complete luxury skincare routine
          </p>
        </motion.div>

        <div className="grid md:grid-cols-3 gap-8">
          {products.map((product, index) => (
            <motion.div
              key={product.id}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.2 }}
              viewport={{ once: true }}
              className="bg-white/80 backdrop-blur-sm border border-white/30 rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 overflow-hidden group"
            >
              <div className="aspect-square overflow-hidden bg-white/50 backdrop-blur-sm border border-white/20 flex items-center justify-center px-8">
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full h-full object-contain group-hover:scale-105 transition-transform duration-300"
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
                <div className="hidden w-full h-full items-center justify-center bg-gradient-to-br from-neoshe/10 to-neoshe/20">
                  <div className="text-center">
                    <div className="w-16 h-16 bg-neoshe/20 rounded-full flex items-center justify-center mx-auto mb-3">
                      <span className="text-2xl">ðŸ§´</span>
                    </div>
                    <p className="text-neoshe font-light">Product Image</p>
                  </div>
                </div>
              </div>
              <div className="p-8 flex flex-col h-full">
                <div className="flex-grow">
                  <h3 className="text-2xl font-regular text-neoshe mb-3">
                    {product.name}
                  </h3>
                  <p className="text-gray-600 mb-4 leading-relaxed">
                    {product.description}
                  </p>
                  
                  {/* Product Features */}
                  <div className="mb-6">
                    <div className="flex flex-wrap gap-2">
                      {product.features.map((feature, index) => (
                        <span
                          key={index}
                          className="px-3 py-1 bg-white/60 backdrop-blur-sm border border-white/40 text-neoshe text-xs rounded-full font-light"
                        >
                          {feature}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>

                <div className="flex items-center justify-between h-12 mt-auto pt-4 border-t border-gray-100">
                  <span className="text-2xl font-light text-neoshe">
                    {product.price}
                  </span>
                  <button className="bg-neoshe hover:bg-neoshe-dark text-white px-8 py-3 rounded-full text-sm font-light transition-all duration-300 transform hover:scale-105 shadow-md hover:shadow-lg h-10 flex items-center">
                    Shop Now
                  </button>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Products;
