import React from 'react';
import { motion } from 'framer-motion';

const Products = () => {
  const products = [
    {
      id: 1,
      name: "100% Natural Moisture Cream",
      description: "Collagen booster with certified organic ingredients for deep hydration and skin renewal",
      image: "/images/products/moisture-cream.jpg",
      price: "$52",
      features: ["100% Natural", "Collagen Booster", "Certified Organic", "50 ML"]
    },
    {
      id: 2,
      name: "SPF 50+ Sunscreen",
      description: "Broad spectrum UVA + UVB protection with natural ingredients for daily sun care",
      image: "/images/products/sunscreen.jpg",
      price: "$38",
      features: ["SPF 50+", "UVA + UVB Filter", "100% Natural", "50 ML"]
    },
    {
      id: 3,
      name: "Purifying Cleanser",
      description: "Gentle daily cleanser with organic extracts for clean, refreshed skin",
      image: "/images/products/cleanser.jpg",
      price: "$45",
      features: ["100% Natural", "Gentle Formula", "Certified Organic", "50 ML"]
    }
  ];

  return (
    <section id="products" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-light text-neoshe mb-6">
            Our Signature Collection
          </h2>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Three essential products for a complete luxury skincare routine
          </p>
        </motion.div>

        <div className="grid md:grid-cols-3 gap-8">
          {products.map((product, index) => (
            <motion.div
              key={product.id}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.2 }}
              viewport={{ once: true }}
              className="bg-white rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 overflow-hidden group"
            >
              <div className="aspect-square overflow-hidden">
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                />
              </div>
              <div className="p-8">
                <h3 className="text-2xl font-medium text-neoshe mb-3">
                  {product.name}
                </h3>
                <p className="text-gray-600 mb-4 leading-relaxed">
                  {product.description}
                </p>
                <div className="flex items-center justify-between">
                  <span className="text-2xl font-light text-neoshe">
                    {product.price}
                  </span>
                  <button className="bg-neoshe hover:bg-neoshe-dark text-white px-6 py-3 rounded-full text-sm font-medium transition-all duration-300 transform hover:scale-105">
                    Shop Now
                  </button>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Products;
